import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ApplicationUserLogin } from 'src/app/models/account/application-user-login.model';
import { AccountService } from 'src/app/services/account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginForm: FormGroup;

  constructor(
    private accountService: AccountService,
    private router: Router,
    private formBuilder: FormBuilder
  ) { 
    if(this.accountService.isLoggedIn){
      this.router.navigate(['/dashboard']);
    }
  }

  ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      username: [null, [
        Validators.required,
        Validators.minLength(5),
        Validators.maxLength(20)
      ]],
      password: [null, [
        Validators.required,
        Validators.minLength(10),
        Validators.maxLength(50)
      ]]
    });
  }

  isTouched(field: string){
    return this.loginForm.get(field).touched;
  }

  hasErrors(field: string) {
    return this.loginForm.get(field).errors;
  }

  hasError(field: string, error: string) {
    return !!this.loginForm.get(field).hasError(error);
  }

  onSubmit() {
    let applicationUserLogin: ApplicationUserLogin = new ApplicationUserLogin(
      this.loginForm.get("username").value,
      this.loginForm.get("password").value
    );

    this.accountService.login(applicationUserLogin).subscribe(() => {
      this.router.navigate(['/dashboard']);
    });
  }

}
<div class="container">
    <div class="row">
        <div class="col-md-6 offset-md-3 mt-5">

            <h3 class="text-dark">Login</h3>
            <p class="text-dark">Get to your dashboard and start blogging</p>

            <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">

                <div class="mb-3">
                    <input
                        type="text"
                        [ngClass]="{'is-invalid' : hasErrors('username') && isTouched('username'),
                                    'is-valid' : !hasErrors('username') && isTouched('username')}"
                        class="form-control form-control-lg"
                        formControlName="username"
                        placeholder="Username">

                    <div *ngIf="hasError('username', 'required')" class="invalid-feedback">
                        Username is required
                    </div>

                    <div *ngIf="hasError('username', 'minlength') || hasError('username', 'maxlength')" 
                        class="invalid-feedback">
                        Must be between 5-20 characters
                    </div>
                </div>

                <div class="mb-3">
                    <input
                        type="password"
                        [ngClass]="{'is-invalid' : hasErrors('password') && isTouched('password'),
                                    'is-valid' : !hasErrors('password') && isTouched('password')}"
                        class="form-control form-control-lg"
                        formControlName="password"
                        placeholder="Password">

                    <div *ngIf="hasError('password', 'required')" class="invalid-feedback">
                        Password is required
                    </div>

                    <div *ngIf="hasError('password', 'minlength') || hasError('password', 'maxlength')" 
                        class="invalid-feedback">
                        Must be between 10-50 characters
                    </div>
                </div>

                <button class="btn btn-block mb-3 bg-primary text-light"
                [disabled]="!loginForm.valid">Login</button>

            </form>

        </div>
    </div>
</div>
